        $('.divBuscadorArticulos #volverAtras1MeterCasa').css({ display: 'none' });
        $('.divBuscadorArticulos #volverAtras125MeterCasa').css({ display: 'block' });
        if (grosor == '30 mm') {
            if (caje == 'SI') {
                aux = aux * 85;
            } else {
                aux = aux * 80;
            }
        } else {
            if (caje == 'SI') {
                aux = aux * 55;
            } else {
                aux = aux * 51;
            }
        }
        if (screen.width < 800) {
            $('#productosPrincipal').css({ height: '2675' });
        }
        $('#textprecioCalculadoraazul').css({ display: 'block' });
