                this.objetoTodoArray = arrayMetInt;
                objetoTODO['interiores'] = arrayMetInt;
                $('#cliccambiarinteriores').attr('onclick', 'interioresNuevoJson(' + JSON.stringify(objetoTODO) + ')');
